import java.awt.* ;public class KeyGrab extends Thread{	Paddle player ;	protected int upkey,downkey,leftkey,rightkey ;	public KeyGrab(Paddle player,int up,int down,int left,int right){		this.player = player ;		upkey = up ;		downkey = down ;		leftkey = left ;		rightkey = right ;	}		public void run(){		while(true) yield() ;	}		public boolean keyDown(Event e, int key){		System.out.println("key detected") ;		if(key == upkey) player.move(1) ;		else if(key  == downkey)	player.move(2) ;		else if(key == leftkey )player.move(3) ;		else if(key == rightkey)player.move(4) ;		else	System.out.println(key) ;		return true ;	}	}